    "scatter = sns.scatterplot(data=opportunity,\n",
    "                          x=\"% of Share of EV in Total Vehicles Sold\",\n",
    "                          y=\"EVs_per_Charging_Station\",\n",
    "                          s=100, color=\"#008080\", edgecolor=\"black\")\n",
    "\n",
    "texts = []\n",
